import ListItems from "../list-items";

function List({ list, setList, filtered, setFiltered }) {

    function checkedAllHandler() {
        setFiltered(filtered.map(el => console.log(el.completed)))

    }

    return (
        <section className='main'>
            <input
                className="toggle-all"
                type="checkbox"
                onClick={checkedAllHandler}
                />
            <label htmlFor="toggle-all">
                Mark all as completed
            </label>

            <ul className="todo-list">
                {
                    filtered.map((item, i) =>
                        <ListItems
                            key={i}
                            item={item}
                            list={list}
                            setList={setList}
                        />
                    )}
            </ul>
        </section>
    );
}
export default List;
