import { useState } from 'react';

const Form = () => {
    const [form, setForm] = useState('')

    function changeHandler(e) {
        setForm(e.target.value)
        console.log(e.target.value)
    }


    return (
        <section className="todoapp">
            <header class="header">
                <h1>todos</h1>
                <form>
                    <input onChange={changeHandler} class="new-todo" placeholder="What needs to be done?" autoFocus />
                </form>
            </header>
        </section>
    );
}

export default Form
