import { useState, useEffect } from 'react';
import './App.css';
import Form from './components/form'


function App() {
    const [list, setList] = useState([]);

    useEffect( () => {
        localStorage.setItem('item', JSON.stringify(list))
    },[list])

    useEffect( () => {
        setList(JSON.parse(localStorage.getItem('item')))
    },[])


    return (
        <>
            <Form 
            setList={setList}
            />
        </>
    );
}

export default App;
