import ListItems from "../list-items";

function List({ list, setList, filtered }) {

    return (
        <section className='main'>
            <input className="toggle-all" type="checkbox" />
            <label htmlFor="toggle-all">
                Mark all as complete
            </label>

            <ul className="todo-list">
                    {
                    filtered.map( (item, i) => 
                    <ListItems 
                        key={i}
                        item={item}
                        list={list}
                        setList={setList}
                    />
                    )}
            </ul>
        </section>
    );
}
export default List;
