import ListItems from "../list-items";

function List({ list, setList, filtered }) {


    function allCheckedHandler() {
        filtered.map(el => el.completed = true)
    }

    return (
        <section className='main'>

            <label htmlFor="toggle-all">
                <input
                    className="toggle-all"
                    type="checkbox"
                    onClick={allCheckedHandler}
                />
            </label>

            <ul className="todo-list">
                {
                    filtered.map((item, i) =>
                        <ListItems
                            key={i}
                            item={item}
                            list={list}
                            setList={setList}
                        />
                    )}
            </ul>
        </section>
    );
}
export default List;
