import { useState } from 'react';


function ListItems({ item, list, setList, filtered }) {
    const [checked, setChecked] = useState(false);

    function completeHandle() {
        setList(list.map(el => {
            if (el.id === item.id) {
                return {
                    ...el, completed: !el.completed
                }
            } else return el
        }))
    }

    function deleteHandle() {
        setList(list.filter((el) => el.id !== item.id))
    }

    return (
        <>
            <li className={`medet ${item.completed && 'completed'}`}>
                <div className="view">
                    <input 
                    className="toggle" 
                    onClick={completeHandle} 
                    type="checkbox" 
                    checked={filtered.completed} 
                    />
                    <label>
                        {item.task}
                    </label>
                    <button 
                    className="destroy" 
                    onClick={deleteHandle}>
                    </button>
                </div>
            </li>
        </>
    );
}

export default ListItems;
